var Hapi = require('hapi');
 
var server = new Hapi.Server({
  cache: {
    engine: require('..'),
    host: 'localhost',
    port: 6379
  }
});

server.connection({ port: 5000 });
 
server.start(function() {
  console.log('started');
 
  setTimeout(function() {
    server.stop(function() {
      console.log('stopped');
    });
  }, 3000);
});